var addSorting = (function() {
    'use strict';
    var cols,
        currentSort = {
            index: 0,
            desc: false
        };

    // returns the summary table element
    function getTable() {
        return document.querySelector('.coverage-summary');
    }
    // returns the thead element of the summary table
    function getTableHeader() {
        return getTable().querySelector('thead tr');
    }
    // returns the tbody element of the summary table
    function getTableBody() {
        return getTable().querySelector('tbody');
    }
    // returns the th element for nth column
    function getNthColumn(n) {
        return getTableHeader().querySelectorAll('th')[n];
    }

    function onFilterInput() {
        const searchValue = document.getElementById('fileSearch').value;
        const rows = document.getElementsByTagName('tbody')[0].children;
        for (let i = 0; i < rows.length; i++) {
            const row = rows[i];
            if (
                row.textContent
                    .toLowerCase()
                    .includes(searchValue.toLowerCase())
            ) {
                row.style.display = '';
            } else {
                row.style.display = 'none';
            }
        }
    }

    // loads the search box
    function addSearchBox() {
        var template = document.getElementById('filterTemplate');
        var templateClone = template.content.cloneNode(true);
        templateClone.getElementById('fileSearch').oninput = onFilterInput;
        template.parentElement.appendChild(templateClone);
    }

    // loads all columns
    function loadColumns() {
        var colNodes = getTableHeader().querySelectorAll('th'),
            colNode,
            cols = [],
            col,
            i;

        for (i = 0; i < colNodes.length; i += 1) {
            colNode = colNodes[i];
            col = {
                key: colNode.getAttribute('data-col'),
                sortable: !colNode.getAttribute('data-nosort'),
                type: colNode.getAttribute('data-type') || 'string'
            };
            cols.push(col);
            if (col.sortable) {
                col.defaultDescSort = col.type === 'number';
                colNode.innerHTML =
                    colNode.innerHTML + '<span class="sorter"></span>';
            }
        }
        return cols;
    }
    // attaches a data attribute to every tr element with an object
    // of data values keyed by column name
    function loadRowData(tableRow) {
        var tableCols = tableRow.querySelectorAll('td'),
            colNode,
            col,
            data = {},
            i,
            val;
        for (i = 0; i < tableCols.length; i += 1) {
            colNode = tableCols[i];
            col = cols[i];
            val = colNode.getAttribute('data-value');
            if (col.type === 'number') {
                val = Number(val);
            }
            data[col.key] = val;
        }
        return data;
    }
    // loads all row data
    function loadData() {
        var rows = getTableBody().querySelectorAll('tr'),
            i;

        for (i = 0; i < rows.length; i += 1) {
            rows[i].data = loadRowData(rows[i]);
        }
    }
    // sorts the table using the data for the ith column
    function sortByIndex(index, desc) {
        var key = cols[index].key,
            sorter = function(a, b) {
                a = a.data[key];
                b = b.data[key];
                return a < b ? -1 : a > b ? 1 : 0;
            },
            finalSorter = sorter,
            tableBody = document.querySelector('.coverage-summary tbody'),
            rowNodes = tableBody.querySelectorAll('tr'),
            rows = [],
            i;

        if (desc) {
            finalSorter = function(a, b) {
                return -1 * sorter(a, b);
            };
        }

        for (i = 0; i < rowNodes.length; i += 1) {
            rows.push(rowNodes[i]);
            tableBody.removeChild(rowNodes[i]);
        }

        rows.sort(finalSorter);

        for (i = 0; i < rows.length; i += 1) {
            tableBody.appendChild(rows[i]);
        }
    }
    // removes sort indicators for current column being sorted
    function removeSortIndicators() {
        var col = getNthColumn(currentSort.index),
            cls = col.className;

        cls = cls.replace(/ sorted$/, '').replace(/ sorted-desc$/, '');
        col.className = cls;
    }
    // adds sort indicators for current column being sorted
    function addSortIndicators() {
        getNthColumn(currentSort.index).className += currentSort.desc
            ? ' sorted-desc'
            : ' sorted';
    }
    // adds event listeners for all sorter widgets
    function enableUI() {
        var i,
            el,
            ithSorter = function ithSorter(i) {
                var col = cols[i];

                return function() {
                    var desc = col.defaultDescSort;

                    if (currentSort.index === i) {
                        desc = !currentSort.desc;
                    }
                    sortByIndex(i, desc);
                    removeSortIndicators();
                    currentSort.index = i;
                    currentSort.desc = desc;
                    addSortIndicators();
                };
            };
        for (i = 0; i < cols.length; i += 1) {
            if (cols[i].sortable) {
                // add the click event handler on the th so users
                // dont have to click on those tiny arrows
                el = getNthColumn(i).querySelector('.sorter').parentElement;
                if (el.addEventListener) {
                    el.addEventListener('click', ithSorter(i));
                } else {
                    el.attachEvent('onclick', ithSorter(i));
                }
            }
        }
    }
    // adds sorting functionality to the UI
    return function() {
        if (!getTable()) {
            return;
        }
        cols = loadColumns();
        loadData();
        addSearchBox();
        addSortIndicators();
        enableUI();
    };
})();

window.addEventListener('load', addSorting);

export default async () => {
  try {
    // Remove qualquer expect injetado previamente (ex.: vitest) para evitar conflito
    if (globalThis.expect && globalThis.expect._expectVitest) {
      delete globalThis.expect;
    }
    // Também remove símbolo interno se existir
    const sym = Object.getOwnPropertySymbols(globalThis).find(s => String(s).includes('$$jest-matchers-object'));
    if (sym) {
      try { delete globalThis[sym]; } catch {}
    }
  } catch {}
};

import { Teladelogin } from "./components/ui/paginas/Teladelogin";
import Tela_Home from "./components/ui/paginas/Tela_Home";
import { EditDoacoes } from "./components/ui/paginas/EditDoacoes";
import { HomeRealocacao } from "./components/ui/paginas/HomeRealocacao";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import { SolicitarDoacao } from "./components/ui/paginas/SolicitarDoacao";
import { PostagemRealocacao } from "./components/ui/paginas/PostagemRealocacao";
import TodasDoacoes from "./components/ui/paginas/TodasDoacoes";
import TelahomeONG from "./components/ui/paginas/TelahomeONG";
import { RealocacaoListagem } from "./components/ui/paginas/RealocacaoListagem";
import ConfirmacaoEncerrarSolicitacao from "./components/ui/paginas/ConfirmacaoEncerrarSolicitacao";
import ConfirmacaoEncerrarRealocacao from "./components/ui/paginas/ConfirmacaoEncerrarRealocacao";
import { DataProvider } from "./context/DataContext";
import { AuthProvider } from "./context/AuthContext";
import ConfirmacaoDeletar from "./components/ui/paginas/ConfirmacaoDeletar";
import TelaFlutuante from "./components/ui/TelaFlutuante";
import BackendConnectionTest from "./components/BackendConnectionTest";
import React, { useState } from "react";
import ProtectedRoute from "./components/ProtectedRoute";

function App() {
  // Imagens do carrossel (separadas)
  const imagensCarrossel = [
    "https://blog.brlogic.com/pt/wp-content/uploads/sites/6/2023/03/radio-online-para-ongs-990x621.jpg",
    "https://images.squarespace-cdn.com/content/v1/56b10ce8746fb97c2d267b79/1562945026360-WP41EW2905VXP5VKPAEG/relaotior+ods1.png?format=1500w",
    "https://jpimg.com.br/uploads/2023/05/8-ongs-que-atuam-pelos-animais-no-brasil.jpg"
  ];

    const [telaFlutuanteVisible, setTelaFlutuanteVisible] = useState(false);

    const abrirTelaFlutuante = () => {
      setTelaFlutuanteVisible(true);
    };

    const fecharTelaFlutuante = () => {
      setTelaFlutuanteVisible(false);
    };

  return (
    <AuthProvider>
      <DataProvider>
        <BrowserRouter>
        <Routes>
          {/* Rotas públicas */}
          <Route path="/" element={<Tela_Home imagensCarrossel={imagensCarrossel} />} />
          <Route path="/login" element={<Teladelogin />} />
          <Route path="/todas-doacoes" element={<TodasDoacoes />} />
          <Route path="/realocacao-listagem" element={<RealocacaoListagem />} />
          
          {/* Rotas protegidas - requerem autenticação */}
          <Route path="/edit-doacoes" element={
            <ProtectedRoute>
              <EditDoacoes />
            </ProtectedRoute>
          } />
          <Route path="/home-realocacao" element={
            <ProtectedRoute>
              <HomeRealocacao />
            </ProtectedRoute>
          } />
          <Route path="/solicitar-doacao" element={
            <ProtectedRoute>
              <SolicitarDoacao />
            </ProtectedRoute>
          } />
          <Route path="/postagem-realocacao" element={
            <ProtectedRoute>
              <PostagemRealocacao />
            </ProtectedRoute>
          } />
          <Route path="/home-ong" element={
            <ProtectedRoute>
              <TelahomeONG imagensCarrossel={imagensCarrossel} />
            </ProtectedRoute>
          } />
          <Route path="/confirmar-encerrar-solicitacao" element={
            <ProtectedRoute>
              <ConfirmacaoEncerrarSolicitacao />
            </ProtectedRoute>
          } />
          <Route path="/confirmar-encerrar-realocacao" element={
            <ProtectedRoute>
              <ConfirmacaoEncerrarRealocacao />
            </ProtectedRoute>
          } />
          <Route path="/confirmar-deletar" element={
            <ProtectedRoute>
              <ConfirmacaoDeletar />
            </ProtectedRoute>
          } />
        </Routes>

        <TelaFlutuante 
          isVisible={telaFlutuanteVisible}
          onClose={fecharTelaFlutuante}
          nomeONG="Instituto Beneficente"
        />
        
        {/* Componente para monitorar conexão com backend - TEMPORARIAMENTE DESABILITADO */}
        {/* <BackendConnectionTest /> */}
        </BrowserRouter>
      </DataProvider>
    </AuthProvider>
  );}

export default App;

import React, { useState, useEffect } from 'react';
import api from '../services/api';

const BackendConnectionTest = () => {
  const [connectionStatus, setConnectionStatus] = useState('checking');
  const [backendData, setBackendData] = useState(null);
  const [error, setError] = useState(null);

  const testConnection = async () => {
    setConnectionStatus('checking');
    setError(null);
    
    try {
      // Tentar conectar na rota principal do backend
      const response = await fetch('http://localhost:3000/', {
        method: 'GET',
        headers: {
          'Accept': 'text/plain'
        }
      });
      
      if (response.ok) {
        const text = await response.text();
        console.log('✅ Backend conectado:', text);
        setConnectionStatus('connected');
        
        // Testar endpoint de doações
        await testDoacoesEndpoint();
      } else {
        setConnectionStatus('disconnected');
        setError('Backend respondeu com erro: ' + response.status);
      }
    } catch (err) {
      console.error('❌ Erro ao conectar com backend:', err);
      setConnectionStatus('disconnected');
      setError('Não foi possível conectar com o backend na porta 3000');
    }
  };

  const testDoacoesEndpoint = async () => {
    try {
      const response = await api.get('/doacoes');
      console.log('✅ Endpoint /doacoes funcionando:', response.data);
      setBackendData(response.data);
    } catch (err) {
      console.error('❌ Erro no endpoint /doacoes:', err);
      setError('Endpoint /doacoes não está funcionando: ' + err.message);
    }
  };

  useEffect(() => {
    testConnection();
  }, []);

  const getStatusColor = () => {
    switch (connectionStatus) {
      case 'connected': return 'text-green-600';
      case 'disconnected': return 'text-red-600';
      case 'checking': return 'text-yellow-600';
      default: return 'text-gray-600';
    }
  };

  const getStatusText = () => {
    switch (connectionStatus) {
      case 'connected': return '✅ Conectado';
      case 'disconnected': return '❌ Desconectado';
      case 'checking': return '🔄 Verificando...';
      default: return '❓ Desconhecido';
    }
  };

  return (
    <div className="fixed bottom-4 right-4 bg-white p-4 rounded-lg shadow-lg border border-gray-200 max-w-sm z-50">
      <div className="flex items-center justify-between mb-2">
        <h3 className="font-semibold text-sm">Status do Backend</h3>
        <button
          onClick={testConnection}
          className="text-xs bg-blue-500 text-white px-2 py-1 rounded hover:bg-blue-600"
        >
          Testar
        </button>
      </div>
      
      <div className={`text-sm font-medium ${getStatusColor()}`}>
        {getStatusText()}
      </div>
      
      {error && (
        <div className="text-xs text-red-600 mt-1">
          {error}
        </div>
      )}
      
      {connectionStatus === 'connected' && backendData && (
        <div className="text-xs text-green-600 mt-1">
          Dados carregados: {Array.isArray(backendData) ? backendData.length : 'objeto'} itens
        </div>
      )}
      
      {connectionStatus === 'disconnected' && (
        <div className="text-xs text-gray-600 mt-2">
          <p>Para conectar com o backend:</p>
          <ol className="list-decimal list-inside mt-1 space-y-1">
            <li>Clone o repositório do backend</li>
            <li>Execute <code className="bg-gray-100 px-1 rounded">npm install</code></li>
            <li>Execute <code className="bg-gray-100 px-1 rounded">npm start</code></li>
          </ol>
        </div>
      )}
    </div>
  );
};

export default BackendConnectionTest;

import React, { useState } from 'react';
import api from '../services/api';

export const NetworkDiagnostic = () => {
  const [result, setResult] = useState('');
  const [testing, setTesting] = useState(false);

  const testConnection = async () => {
    setTesting(true);
    setResult('🔍 Testando diferentes configurações...\n');
    
    // Testar diferentes URLs base
    const baseUrls = [
      'http://localhost:3000',
      'http://localhost:3001',
      'http://localhost:8000',
      'http://localhost:5000'
    ];
    
    // Testar diferentes endpoints de login
    const loginEndpoints = [
      '/auth/login',
      '/api/auth/login', 
      '/login'
    ];
    
    try {
      // Teste 1: Verificar se backend está rodando
      setResult(prev => prev + '1️⃣ Testando se backend está rodando...\n');
      
      for (const baseUrl of baseUrls) {
        try {
          const response = await fetch(baseUrl);
          setResult(prev => prev + `✅ ${baseUrl}: Backend respondendo (${response.status})\n`);
          break;
        } catch (error) {
          setResult(prev => prev + `❌ ${baseUrl}: ${error.message}\n`);
        }
      }
      
      // Teste 2: Testar endpoints de login
      setResult(prev => prev + '\n2️⃣ Testando endpoints de login...\n');
      
      for (const baseUrl of ['http://localhost:3000']) {
        for (const endpoint of loginEndpoints) {
          try {
            setResult(prev => prev + `   Testando: ${baseUrl}${endpoint}\n`);
            
            const response = await fetch(`${baseUrl}${endpoint}`, {
              method: 'POST',
              headers: { 
                'Content-Type': 'application/json',
                'Accept': 'application/json'
              },
              body: JSON.stringify({
                email_ong: 'ong1@gmail.com',
                password: '123456'
              })
            });
            
            const data = await response.text();
            
            if (response.status === 404) {
              setResult(prev => prev + `   ❌ 404 - Endpoint não existe\n`);
            } else if (response.status === 401) {
              setResult(prev => prev + `   ✅ Endpoint existe! (401 = credenciais inválidas)\n`);
            } else if (response.status === 400) {
              setResult(prev => prev + `   ✅ Endpoint existe! (400 = dados inválidos)\n`);
            } else if (response.status === 200) {
              setResult(prev => prev + `   ✅ Login funcionou!\n`);
              setResult(prev => prev + `   Response: ${data.substring(0, 100)}...\n`);
            } else {
              setResult(prev => prev + `   ⚠️ Status ${response.status}: ${data.substring(0, 50)}...\n`);
            }
            
          } catch (error) {
            setResult(prev => prev + `   ❌ Erro: ${error.message}\n`);
          }
        }
      }
      
      // Teste 3: Verificar se é problema de CORS
      setResult(prev => prev + '\n3️⃣ Verificando CORS...\n');
      try {
        const response = await fetch('http://localhost:3000', {
          method: 'OPTIONS',
          headers: {
            'Origin': 'http://localhost:5174',
            'Access-Control-Request-Method': 'POST',
            'Access-Control-Request-Headers': 'Content-Type'
          }
        });
        setResult(prev => prev + `✅ CORS: ${response.status}\n`);
      } catch (error) {
        setResult(prev => prev + `❌ CORS problema: ${error.message}\n`);
      }
      
    } catch (error) {
      setResult(prev => prev + `❌ Erro geral: ${error.message}\n`);
    } finally {
      setTesting(false);
    }
  };

  return (
    <div className="mt-4 p-4 bg-gray-50 rounded-lg">
      <h3 className="text-sm font-medium text-gray-700 mb-2">🔧 Diagnóstico de Rede</h3>
      <button
        onClick={testConnection}
        disabled={testing}
        className="mb-3 px-3 py-1 bg-blue-600 text-white text-xs rounded hover:bg-blue-700 disabled:opacity-50"
      >
        {testing ? 'Testando...' : 'Testar Conexão'}
      </button>
      {result && (
        <pre className="text-xs text-gray-600 bg-white p-2 rounded border whitespace-pre-wrap">
          {result}
        </pre>
      )}
    </div>
  );
};

export default NetworkDiagnostic;

import React from 'react';
import { Navigate, useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

export const ProtectedRoute = ({ children }) => {
  const { isAuthenticated, loading } = useAuth();
  const location = useLocation();

  // Mostrar loading enquanto verifica autenticação
  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  // Se não está autenticado, redirecionar para a home pública
  if (!isAuthenticated) {
    return <Navigate to="/" replace />;
  }

  // Se está autenticado, renderizar o componente
  return children;
};

export default ProtectedRoute;

