import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { useData } from '@/context/DataContext';

export function SearchDropdown({ searchTerm, onClose }) {
  const navigate = useNavigate();
  const { getDoacoesPaginadas, getRealocacoesPaginadas, doacoes, realocacoes } = useData();
  const [results, setResults] = useState({ items: [], pages: [] });

  // Definir páginas disponíveis para busca
  const pages = [
    { title: 'Início', path: '/home-ong', description: 'Página inicial da ONG' },
    { title: 'Necessidades', path: '/todas-doacoes', description: 'Ver todas as necessidades das ONGs' },
    { title: 'Realocações', path: '/realocacao-listagem', description: 'Ver todos os itens para realocação' },
    { title: 'Minhas Solicitações', path: '/edit-doacoes', description: 'Gerenciar suas solicitações de doação' },
    { title: 'Minhas Realocações', path: '/home-realocacao', description: 'Gerenciar suas postagens de realocação' },
  ];

  useEffect(() => {
    if (!searchTerm || searchTerm.length < 2) {
      setResults({ items: [], pages: [] });
      return;
    }

    const searchLower = searchTerm.toLowerCase();
    
    // Buscar em itens (doações e realocações) - usar os dados do estado
    const allDoacoes = doacoes || [];
    const allRealocacoes = realocacoes || [];
    
    const filteredDoacoes = allDoacoes
      .filter(item => !item.encerrado)
      .filter(item => 
        item.titulo?.toLowerCase().includes(searchLower) ||
        item.categoria?.toLowerCase().includes(searchLower) ||
        item.descricao?.toLowerCase().includes(searchLower) ||
        item.ong?.toLowerCase().includes(searchLower)
      )
      .slice(0, 5) // Limitar a 5 resultados
      .map(item => ({ ...item, type: 'doacao' }));

    const filteredRealocacoes = allRealocacoes
      .filter(item => !item.encerrado)
      .filter(item => 
        item.titulo?.toLowerCase().includes(searchLower) ||
        item.categoria?.toLowerCase().includes(searchLower) ||
        item.descricao?.toLowerCase().includes(searchLower) ||
        item.ong?.toLowerCase().includes(searchLower)
      )
      .slice(0, 5) // Limitar a 5 resultados
      .map(item => ({ ...item, type: 'realocacao' }));

    // Buscar em páginas
    const filteredPages = pages
      .filter(page => 
        page.title.toLowerCase().includes(searchLower) ||
        page.description.toLowerCase().includes(searchLower)
      )
      .slice(0, 3); // Limitar a 3 resultados

    setResults({
      items: [...filteredDoacoes, ...filteredRealocacoes].slice(0, 6), // Total de 6 itens
      pages: filteredPages
    });
  }, [searchTerm, doacoes, realocacoes]);

  const handleItemClick = (item) => {
    if (item.type === 'doacao') {
      navigate('/todas-doacoes');
    } else if (item.type === 'realocacao') {
      navigate('/realocacao-listagem');
    }
    onClose();
  };

  const handlePageClick = (page) => {
    navigate(page.path);
    onClose();
  };

  if (!searchTerm || searchTerm.length < 2) {
    return null;
  }

  const hasResults = results.items.length > 0 || results.pages.length > 0;

  return (
    <div data-testid="search-dropdown" className="absolute top-full left-0 right-0 mt-1 bg-white border border-gray-200 rounded-lg shadow-lg z-50 max-h-96 overflow-y-auto">
      {hasResults ? (
        <div className="py-2">
          {/* Seção de Páginas */}
          {results.pages.length > 0 && (
            <div className="mb-2">
              <div className="px-3 py-2 text-xs font-semibold text-gray-500 uppercase tracking-wide border-b">
                Páginas
              </div>
              {results.pages.map((page, index) => (
                <div
                  key={index}
                  data-testid="search-page-item"
                  onClick={() => handlePageClick(page)}
                  className="px-3 py-2 hover:bg-gray-50 cursor-pointer border-b border-gray-100"
                >
                  <div className="flex items-center gap-2">
                    <div className="w-8 h-8 bg-blue-100 rounded flex items-center justify-center">
                      <svg className="w-4 h-4 text-blue-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                      </svg>
                    </div>
                    <div className="flex-1">
                      <div className="text-sm font-medium text-gray-900">{page.title}</div>
                      <div className="text-xs text-gray-500">{page.description}</div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}

          {/* Seção de Itens */}
          {results.items.length > 0 && (
            <div>
              <div className="px-3 py-2 text-xs font-semibold text-gray-500 uppercase tracking-wide border-b">
                Itens
              </div>
              {results.items.map((item, index) => (
                <div
                  key={index}
                  data-testid="search-result-item"
                  onClick={() => handleItemClick(item)}
                  className="px-3 py-2 hover:bg-gray-50 cursor-pointer border-b border-gray-100"
                >
                  <div className="flex items-center gap-2">
                    <img
                      src={item.imageUrl}
                      alt={item.titulo}
                      className="w-10 h-10 object-cover rounded"
                    />
                    <div className="flex-1">
                      <div className="text-sm font-medium text-gray-900">{item.titulo}</div>
                      <div className="text-xs text-gray-500 flex items-center gap-2">
                        <span>{item.ong}</span>
                        <span>•</span>
                        <span className={`px-2 py-1 rounded text-xs ${
                          item.type === 'doacao' 
                            ? 'bg-blue-100 text-blue-800' 
                            : 'bg-green-100 text-green-800'
                        }`}>
                          {item.type === 'doacao' ? 'Necessidade' : 'Realocação'}
                        </span>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      ) : (
        <div className="px-3 py-6 text-center text-gray-500">
          <svg className="w-12 h-12 mx-auto text-gray-300 mb-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
          </svg>
          <div className="text-sm">Nenhum resultado encontrado</div>
          <div className="text-xs text-gray-400">Tente pesquisar por outros termos</div>
        </div>
      )}
    </div>
  );
}

import React, { useState, useEffect } from 'react';

export function TempoRestante({ prazo, publicado, className = "" }) {
  const [tempoRestante, setTempoRestante] = useState("");
  const [status, setStatus] = useState("normal");

  useEffect(() => {
    const calcularTempoRestante = () => {
      if (!prazo) {
        setTempoRestante("Sem prazo definido");
        setStatus("normal");
        return;
      }

      const agora = new Date();
      const dataPrazo = new Date(prazo.split('/').reverse().join('-')); // Converte DD/MM/YYYY para YYYY-MM-DD
      const diferencaTempo = dataPrazo.getTime() - agora.getTime();

      if (diferencaTempo <= 0) {
        setTempoRestante("Expirado");
        setStatus("expirado");
        return;
      }

      const dias = Math.floor(diferencaTempo / (1000 * 60 * 60 * 24));
      const horas = Math.floor((diferencaTempo % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
      const minutos = Math.floor((diferencaTempo % (1000 * 60 * 60)) / (1000 * 60));

      let tempoTexto = "";
      
      if (dias > 0) {
        tempoTexto = `${dias}d ${horas}h`;
      } else if (horas > 0) {
        tempoTexto = `${horas}h ${minutos}m`;
      } else {
        tempoTexto = `${minutos}m`;
      }

      setTempoRestante(tempoTexto);

      // Definir status baseado no tempo restante
      if (dias <= 1) {
        setStatus("urgente");
      } else if (dias <= 7) {
        setStatus("atencao");
      } else {
        setStatus("normal");
      }
    };

    // Calcular imediatamente
    calcularTempoRestante();

    // Atualizar a cada minuto
    const intervalo = setInterval(calcularTempoRestante, 60000);

    return () => clearInterval(intervalo);
  }, [prazo]);

  return (
    <span className={`flex items-center gap-1 text-sm text-gray-500 ${className}`}>
      <svg
        width="16"
        height="16"
        fill="none"
        stroke="currentColor"
        strokeWidth="1.5"
        className="inline-block"
      >
        <circle cx="8" cy="8" r="7" />
        <path d="M8 4v4l2 2" />
      </svg>
      Tempo restante: {tempoRestante}
    </span>
  );
}

export function DataAtual({ className = "" }) {
  const [dataAtual, setDataAtual] = useState("");

  useEffect(() => {
    const atualizarData = () => {
      const agora = new Date();
      const opcoes = {
        weekday: 'long',
        year: 'numeric',
        month: 'long',
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit',
        second: '2-digit',
        timeZone: 'America/Recife'
      };
      
      const dataFormatada = agora.toLocaleDateString('pt-BR', opcoes);
      setDataAtual(dataFormatada);
    };

    // Atualizar imediatamente
    atualizarData();

    // Atualizar a cada segundo
    const intervalo = setInterval(atualizarData, 1000);

    return () => clearInterval(intervalo);
  }, []);

  return (
    <div className={`inline-flex items-center gap-2 text-sm text-gray-600 ${className}`}>
      <svg
        width="16"
        height="16"
        fill="none"
        stroke="currentColor"
        strokeWidth="1.5"
        className="inline-block"
      >
        <circle cx="8" cy="8" r="7" />
        <path d="M8 4v4l2 2" />
      </svg>
      <span>{dataAtual}</span>
    </div>
  );
}

