		for (let i=0;i<5 && !loaded;i++) {
			try {
				await page.goto('/todas-doacoes', { waitUntil: 'domcontentloaded' });
				loaded = true;
			} catch(e) {
				await page.waitForTimeout(500);
			}
		}
		if(!loaded) throw new Error('Servidor não respondeu em /todas-doacoes');
